query:
-- First delete article_categories for articles with no images
DELETE FROM article_categories WHERE articleId IN (SELECT id FROM articles WHERE featuredImage IS NULL OR featuredImage = '');

-- Then delete the articles themselves
DELETE FROM articles WHERE featuredImage IS NULL OR featuredImage = '';

